mapper.update:
UPDATE solutions
SET category_id=#{categoryId}, title=#{title}, difficulty=#{difficulty}, version=#{version},
    description=#{description}, notes=#{notes}, status=#{status}, updated_at=#{updatedAt},
    reviewed_by=#{reviewedBy}
WHERE id=#{id}

mapper.select:
SELECT s.*, c.name AS category_name, c.icon, c.color, creator.id as created_by_id, creator.username as created_username, reviewer.id as reviewed_by_id, reviewer.username as reviewed_username FROM solutions s JOIN categories c ON s.category_id = c.id LEFT JOIN users creator ON s.created_by = creator.id LEFT JOIN users reviewer ON s.reviewed_by = reviewer.id WHERE s.created_by = #{creatorId}
AND s.status = #{status}
ORDER BY s.updated_at DESC LIMIT #{limit} OFFSET #{offset}

SELECT COUNT(*)
FROM solutions
WHERE created_by = #{creatorId}

    AND status = #{status}
